INSERT INTO `sr_article` VALUES ('27', 'help', '', '65', '如何注册支付宝账户？', '', '', '', '<p><span style=\"font-family: 微软雅黑;\"><span style=\"font-size: 12px;\">一、如您想在淘宝网上买东西，可在淘宝网上注册支付宝账户。在淘宝网上注册支付宝账户有两种方式：<br/><br/><a href=\"http://help.alipay.com/lab/help_detail.htm?help_id=242941\" target=\"_blank\" seed=\"detailContent-linkT1\" smartracker=\"on\" style=\"text-decoration: none; color: rgb(0, 136, 204); cursor: pointer; outline: none; background: transparent;\">使用Email注册支付宝</a><br/><br/><a href=\"http://help.alipay.com/lab/help_detail.htm?help_id=242935\" target=\"_blank\" seed=\"detailContent-linkT2\" smartracker=\"on\" style=\"text-decoration: none; color: rgb(0, 136, 204); cursor: pointer; outline: none; background: transparent;\">使用手机号码注册支付宝</a></span></span></p><p><br/><br/><span style=\"font-family: 微软雅黑;\"><span style=\"font-size: 12px;\">二、如您想在支付宝网站或外部网站进行交易，可在支付宝网站上注册支付宝账户。</span></span></p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\">1）在支付宝网站注册支付宝账户（<strong>大陆用户</strong>）：</span></p><p><br/><span style=\"font-family: 微软雅黑;\"><span style=\"font-size: 12px;\"><a href=\"http://help.alipay.com/lab/help_detail.htm?help_id=211703#1\" target=\"_blank\" seed=\"detailContent-linkT3\" smartracker=\"on\" style=\"text-decoration: none; color: rgb(0, 136, 204); cursor: pointer; outline: none; background: transparent;\">用手机号码注册</a><br/><br/><a href=\"http://help.alipay.com/lab/help_detail.htm?help_id=211703#9\" target=\"_blank\" seed=\"detailContent-linkT4\" smartracker=\"on\" style=\"text-decoration: none; color: rgb(0, 136, 204); cursor: pointer; outline: none; background: transparent;\">用邮箱注册</a></span></span></p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\">2）在支付宝钱包注册支付宝账户（<strong>大陆用户</strong>）：</span></p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\"><a href=\"https://cshall.alipay.com/lab/help_detail.htm?help_id=522222\" target=\"_blank\" seed=\"detailContent-linkT5\" smartracker=\"on\" style=\"text-decoration: none; color: rgb(0, 136, 204); cursor: pointer; outline: none; background: transparent;\">通过支付宝钱包注册</a></span></p><p>&nbsp;</p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\"><span style=\"font-size: 12px;\">三、<strong>非大陆用户</strong></span></span></p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\"><span style=\"font-size: 12px;\">香港、澳门、台湾、海外（韩国、日本、新加坡、马来西亚、美国、英国、澳大利亚、加拿大、泰国、越南、德国、法国、俄罗斯、新西兰、印度尼西亚、荷兰、瑞典、乌克兰、菲律宾、意大利）；</span></span></p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\">1）在支付宝网站注册支付宝账户（<strong>非大陆用户</strong>）：</span></p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\"><a href=\"https://cshall.alipay.com/lab/help_detail.htm?help_id=254355\" target=\"_blank\" seed=\"detailContent-linkT6\" smartracker=\"on\" style=\"text-decoration: none; color: rgb(0, 136, 204); cursor: pointer; outline: none; background: transparent;\">用邮箱注册</a></span></p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\"><a href=\"https://cshall.alipay.com/lab/help_detail.htm?help_id=479405\" target=\"_blank\" seed=\"detailContent-linkT7\" smartracker=\"on\" style=\"text-decoration: none; color: rgb(0, 136, 204); cursor: pointer; outline: none; background: transparent;\">用手机号码注册</a></span></p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\">2）在支付宝钱包注册支付宝账户（<strong>非大陆用户</strong>）</span></p><p>&nbsp;</p><p><span style=\"font-family: 微软雅黑;\"><a href=\"https://cshall.alipay.com/lab/help_detail.htm?help_id=522227\" target=\"_blank\" seed=\"detailContent-linkT8\" smartracker=\"on\" style=\"text-decoration: none; color: rgb(0, 136, 204); cursor: pointer; outline: none; background: transparent;\">通过支付宝钱包注册</a></span></p><p>&nbsp;</p><p><br/></p>', '0', '0', '', '', '0', '231', '231', '0', '-1', '2015-08-01 16:53:54', '2015-08-01 16:10:30');
INSERT INTO `sr_article` VALUES ('28', 'help', '', '47', '如何将金额充值到余额宝', '', '', '', '<p><span style=\"color: rgb(77, 77, 77); font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; background-color: rgb(255, 255, 255);\">1、&nbsp;</span><a href=\"http://www.alipay.com/\" target=\"_blank\" seed=\"detailContent-linkT1\" smartracker=\"on\" style=\"text-decoration: none; color: rgb(0, 136, 204); cursor: pointer; outline: none; font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; white-space: normal; background: rgb(255, 255, 255);\">www.alipay.com</a><span style=\"color: rgb(77, 77, 77); font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; background-color: rgb(255, 255, 255);\">登陆支付宝账户——点击【充值】；</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"\" src=\"https://tfsimg.alipay.com/images/kbmng/T1opReXoXIXXXXXXXX\" seed=\"detailContent-tfsimgImagesKbmngT1opReXoXIXXXXXXXXT1\" smartracker=\"on\" style=\"border: 1px solid; width: 600px; height: 310px;\"/></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; white-space: normal; background-color: rgb(255, 255, 255);\">2、选择【充值到余额宝】——填写转入金额——【同意协议并继续】；</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"line-height: 25px;\"><img alt=\"\" src=\"https://tfsimg.alipay.com/images/kbmng/T1hVReXd8KXXXXXXXX\" seed=\"detailContent-tfsimgImagesKbmngT1hVReXd8KXXXXXXXXT1\" smartracker=\"on\" style=\"border: 1px solid; width: 600px; height: 496px;\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; white-space: normal; background-color: rgb(255, 255, 255);\">3、进入收银台，可以选择储蓄卡<span style=\"color: rgb(0, 0, 0); line-height: 18px;\">进行充值</span>。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(77, 77, 77); font-family: tahoma, arial, 宋体; font-size: 14px; line-height: 25.2000007629395px; white-space: normal; background-color: rgb(255, 255, 255);\"><img alt=\"\" src=\"https://tfsimg.alipay.com/images/kbmng/T1Z0FdXjhlXXXXXXXX\" seed=\"detailContent-tfsimgImagesKbmngT1Z0FdXjhlXXXXXXXXT1\" smartracker=\"on\" style=\"border: 0px; width: 602px; height: 288px;\"/></p><p><br/></p>', '0', '0', '', '', '0', '231', '231', '0', '-1', '2015-08-01 16:53:14', '2015-08-01 16:11:21');
INSERT INTO `sr_article` VALUES ('29', 'help', '', '46', '如何发布任务？', '', '', '', '<p style=\"border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(108, 108, 108); font-family: Arial, Helvetica, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"><br/><span style=\"border: 0px; margin: 0px; padding: 0px; font-family: SimSun; font-size: 15px;\">①先登录熊猫演威客网(没有账号的请先进行注册)，点击右侧“发布需求”；</span></p><p style=\"border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(108, 108, 108); font-family: Arial, Helvetica, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;</p><p style=\"border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(108, 108, 108); font-family: Arial, Helvetica, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;</p><p style=\"border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(108, 108, 108); font-family: Arial, Helvetica, sans-serif; font-size: 12px; white-space: normal; text-align: center; background-color: rgb(255, 255, 255);\"><img src=\"/Uploads/UserFile/Image/231/20150801/55bc7fd104ecb.png\" alt=\"\" original-title=\"\" style=\"border: 0px; margin: 0px; padding: 0px; color: transparent; font-size: 0px; vertical-align: middle;\"/><br/></p><p style=\"border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(108, 108, 108); font-family: Arial, Helvetica, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"border: 0px; margin: 0px; padding: 0px; font-family: &#39;Microsoft YaHei&#39;; font-size: 16px;\"></span>&nbsp;</p><p style=\"border: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(108, 108, 108); font-family: Arial, Helvetica, sans-serif; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"border: 0px; margin: 0px; padding: 