-- ----------------------------
-- Table structure for sr_shop
-- ----------------------------
DROP TABLE IF EXISTS `sr_shop`;
CREATE TABLE `sr_shop` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(100) DEFAULT '' COMMENT '名称',
  `slogan` varchar(255) DEFAULT '' COMMENT '标语',
  `type` tinyint(1) DEFAULT '0' COMMENT '店铺类型：1个人译者，2翻译公司',
  `member_id` int(11) DEFAULT '0' COMMENT '店铺所属用户',
  `logo` varchar(255) DEFAULT '' COMMENT '店铺logo',
  `keywords` varchar(255) DEFAULT '' COMMENT '关键字',
  `short_description` varchar(300) DEFAULT '' COMMENT '店铺简介',
  `level` int(255) DEFAULT '0' COMMENT '店铺等级,level表中type为2 的数据；',
  `status` tinyint(1) DEFAULT '0' COMMENT '0表示正在审核 1表示审核通过 2表示冻结 3表示还未审核',
  `recommend` tinyint(1) DEFAULT '0' COMMENT '是否推荐：0不推荐1推荐',
  `add_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  `theme_id` int(11) DEFAULT '0' COMMENT '模板ID',
  `have_theme` varchar(255) DEFAULT '' COMMENT '商铺拥有的模板ID(用英文逗号隔开)',
  `good_at` varchar(255) DEFAULT '' COMMENT '擅长：categoryID(json）',
  `shop_license` varchar(100) DEFAULT '' COMMENT '如果是个人译者那么这个字段表示身份证照片,如果是公司就是营业执照',
  `shop_tax` varchar(100) DEFAULT '' COMMENT '如果是个人译者就是学历证书，如果是公司的话就是税务登记证',
  `translate_type` varchar(20) DEFAULT '1' COMMENT '0表示中译外 1表示外译外 2表示外译中',
  `translate_year` tinyint(1) DEFAULT '0' COMMENT '表示翻译的年限',
  `comment_num` int(11) DEFAULT '0' COMMENT '评论总数量',
  `total_translate_time` bigint(11) DEFAULT '0' COMMENT '服务总时长: 计量单位  /秒',
  `translate_num` varchar(100) DEFAULT '0' COMMENT '表示日翻译量',
  `service_times` int(11) DEFAULT '0' COMMENT '服务次数： 计量单位 /次',
  `total_translate_num` bigint(11) DEFAULT '0' COMMENT '总翻译字数：计量单位 字',
  `regtime` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '成立时间',
  `area_id` int(11) DEFAULT '0' COMMENT '表示店铺所属地区的id',
  `address` varchar(255) DEFAULT '' COMMENT '地区path',
  `operate_type` varchar(100) DEFAULT '' COMMENT '店铺的业务范围json格式',
  `description_now` text COMMENT '储存店铺介绍',
  `work_time_s` varchar(30) DEFAULT '' COMMENT '工作时间开始',
  `work_time_e` varchar(30) DEFAULT '' COMMENT '工作时间结束',
  `speed_star_num` tinyint(2) DEFAULT '0' COMMENT '速度星数(1-10)',
  `quality_star_num` tinyint(2) DEFAULT '0' COMMENT '质量星数(1-10)',
  `serve_star_num` tinyint(2) DEFAULT '0' COMMENT '服务星数(1-10)',
  `star_num` tinyint(2) DEFAULT '0' COMMENT '综合星数(1-10)',
  `is_identity_confirm` tinyint(1) DEFAULT '0' COMMENT '身份是否确认：0 未确认，1已确认',
  `is_qualified_comfirm` tinyint(1) DEFAULT '0' COMMENT '资质是否确认：0未确认，1已确认',
  `min_price` decimal(15,2) DEFAULT '0.00' COMMENT '店铺最低价，每次发布产品更新',
  `shop_license_1` varchar(100) DEFAULT '' COMMENT '如果是个人译者那么这个字段表示身份证照片反面,如果是公司就是营业执照',
  `have_product` tinyint(1) DEFAULT '0' COMMENT '店铺有无产品(0=>无产品,1有产品)',
  `reason` varchar(255) DEFAULT '' COMMENT '审核不通过原因',
  `be_x` decimal(10,6) DEFAULT '116.403963' COMMENT '店铺所在地区经度',
  `be_y` decimal(10,6) DEFAULT '39.915112' COMMENT '店铺所在地区纬度',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=48 DEFAULT CHARSET=utf8 COMMENT='店铺信息表';

